#* ============================= Modules Personnels ===========================

#* ============================= Modules Communs ==============================



#!------------------------------------------------------------------------------
#!                                   CLASS DEFINITION
#!------------------------------------------------------------------------------



#!------------------------------------------------------------------------------
#!                                 FUNCTION DEFINITION
#!------------------------------------------------------------------------------
def power_law(v=None, z=None, vhub=None, zhub=None, alpha=0.2):
    if v is None:
        v = vhub * (z/zhub)**alpha
        return v
    if vhub is None:
        vhub = v / (z/zhub)**alpha
        return vhub


#!------------------------------------------------------------------------------
#!                                    MAIN FUNCTION
#!------------------------------------------------------------------------------
def main():
    print("v at 10 m:", power_law(z=10, vhub=25, zhub=80))
    print("v at 80 m:", power_law(v=16.493848884661176, z=10, zhub=80))
    

#!------------------------------------------------------------------------------
#!                                     RUNNING TEST
#!------------------------------------------------------------------------------
if __name__ == '__main__':
        main()
